DROP TABLE IF EXISTS `pedidosventa`;
CREATE TABLE `pedidosventa` (
  `codPedido` int(11) NOT NULL AUTO_INCREMENT,
  `codAlmacen` int(11) NOT NULL,
  `codTipoDocumento` int(11) NOT NULL,
  `codCotizacion` int(11) DEFAULT NULL,
  `tipocliente` int(1) NOT NULL,
  `codCliente` int(11) NOT NULL,
  `moneda` int(1) NOT NULL,
  `tipocambio` decimal(10,3) NOT NULL DEFAULT '1.000',
  `fechapedido` datetime NOT NULL,
  `fechaentrega` datetime NOT NULL,
  `codAutorizado` int(3) DEFAULT NULL,
  `codListaPrecio` int(11) DEFAULT NULL,
  `comentario` varchar(120) COLLATE utf8_spanish_ci DEFAULT NULL,
  `bruto` decimal(10,4) NOT NULL,
  `montodscto` decimal(10,4) NOT NULL,
  `igv` decimal(10,4) DEFAULT NULL,
  `total` decimal(10,4) NOT NULL,
  `estado` bit(1) NOT NULL DEFAULT b'1',
  `pendiente` bit(1) NOT NULL DEFAULT b'1',
  `formapago` int(11) DEFAULT NULL,
  `fechapago` datetime DEFAULT NULL,
  `codUsuario` int(11) NOT NULL,
  `fecharegistro` datetime NOT NULL,
  PRIMARY KEY (`codPedido`),
  KEY `codAlmacen` (`codAlmacen`) USING BTREE,
  KEY `pedidosventa_FKIndex1` (`codCliente`) USING BTREE,
  KEY `pedidosventa_FKIndex2` (`codUsuario`) USING BTREE,
  KEY `pedidosventa_FKIndex4` (`codTipoDocumento`) USING BTREE,
  CONSTRAINT `pedidosventa_ibfk_1` FOREIGN KEY (`codCliente`) REFERENCES `cliente` (`codCliente`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `pedidosventa_ibfk_2` FOREIGN KEY (`codUsuario`) REFERENCES `usuario` (`codUsuario`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `pedidosventa_ibfk_3` FOREIGN KEY (`codTipoDocumento`) REFERENCES `tipodocumento` (`codTipoDocumento`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `pedidosventa_ibfk_4` FOREIGN KEY (`codAlmacen`) REFERENCES `almacen` (`codAlmacen`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;

