-- ----------------------------
-- Procedure structure for ActualizaListaDependientes
-- ----------------------------
DROP PROCEDURE IF EXISTS `ActualizaListaDependientes`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ActualizaListaDependientes`(codlista int(11), codpro int(11))
BEGIN

DECLARE v_finished 		INTEGER 	DEFAULT 0;
DECLARE v_codlista		INTEGER 	DEFAULT 0;
DECLARE v_preprom		DOUBLE 	DEFAULT 0;
DECLARE v_margen		DOUBLE 	DEFAULT 0;
DECLARE v_precioneto 	DOUBLE 	DEFAULT 0;
DECLARE v_precio 		DOUBLE 	DEFAULT 0;
DECLARE v_IGV		DOUBLE 	DEFAULT 1;
DECLARE v_origen		INTEGER 	DEFAULT 0;
DECLARE v_decimales	INTEGER 	DEFAULT 0;


DECLARE lista_cursor CURSOR FOR 
SELECT codListaPrecio FROM listaprecio WHERE estado = 1 AND listaorigen = codlista;

DECLARE CONTINUE HANDLER 
FOR NOT FOUND SET v_finished = 1;

OPEN lista_cursor;
update_listas: LOOP
		 
FETCH lista_cursor INTO v_codlista;

SELECT listaorigen,  decimales
INTO v_origen,  v_decimales 
FROM listaprecio 
WHERE codListaPrecio = v_codlista;
		

			SELECT DISTINCT(precioneto) INTO v_preprom FROM detallelistaprecio WHERE codProducto = codpro AND codListaPrecio = v_origen;
			SELECT variacion INTO v_margen FROM listaprecio WHERE codListaPrecio = v_codlista;
			SELECT CASE p.igv WHEN 0 THEN 1 WHEN 1 THEN (SELECT cn.tasaIGV FROM configuracion cn WHERE cn.estado = 1) END INTO v_IGV FROM producto p WHERE p.codProducto = codpro;

			IF v_finished = 1 THEN 
			LEAVE update_listas;
			END IF;

			IF v_decimales = -1 THEN
				SET v_precioneto = v_preprom ;
				SET v_precio = v_precioneto * (1+(v_IGV/100));
			ELSE
				SET v_precioneto = ROUND(v_preprom * (1+(v_margen/100)), v_decimales) ;
				SET v_precio = ROUND(v_precioneto * (1+(v_IGV/100)),v_decimales);
			END IF;
													
			 IF EXISTS(SELECT * FROM detallelistaprecio dl WHERE dl.codProducto = codpro AND dl.codListaPrecio = v_codlista)
			 THEN
							UPDATE 	detallelistaprecio
							SET    	valor = v_preprom,
										margen = v_margen,
										precioneto = v_precioneto,
										precio = v_precio
							WHERE codProducto = codpro AND codListaPrecio = v_codlista;
			  ELSE
				INSERT INTO detallelistaprecio (codProducto,codListaPrecio,valor,margen,precioneto,precio) 
				VALUES (codpro, v_codlista,v_preprom, v_margen,v_precioneto, v_precio);
			  END IF;	

				CALL ActualizaListaDependientes(v_codlista, codpro);		

END LOOP update_listas;
CLOSE lista_cursor;


