-- ----------------------------
-- Procedure structure for AtenderTransferencia
-- ----------------------------
DROP PROCEDURE IF EXISTS `AtenderTransferencia`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `AtenderTransferencia`(codigo int(11), cod int(11), numero varchar(30), Usu int(11))
BEGIN

-- 176, 10, 2061, 5

declare vb_termina int default 0;
DECLARE cant FLOAT DEFAULT 0;
DECLARE valor	FLOAT DEFAULT 0;
DECLARE valors	FLOAT DEFAULT 0;
DECLARE precio	FLOAT DEFAULT 0;
DECLARE codPro	INTEGER DEFAULT 0;
DECLARE codAlma	INTEGER DEFAULT 0;
DECLARE usuI	INTEGER DEFAULT 0;
DECLARE codAlmaORIG	INTEGER DEFAULT 0;
DECLARE req	INTEGER DEFAULT 0;


DECLARE producto_cursor CURSOR FOR
SELECT dt.cantidad, dt.valoreal, dt.valorrealsoles, dt.precioreal, dt.codProducto, dt.codAlmacen, dt.codUser, dt.coddetallerequerimiento
FROM detallenotaingreso dt
where dt.codNotaIngreso=codigo;

DECLARE CONTINUE HANDLER 
		FOR NOT FOUND
		SET vb_termina = 1;

OPEN producto_cursor;
Recorre_Cursor: LOOP


FETCH producto_cursor INTO cant, valor,valors,precio,codPro,codAlma, usuI,req;


SELECT codAlmacen INTO codAlmaORIG FROM notasalida where codTransaccion = 15 and codSerie=cod and numdocumento=numero AND documentoreferencia = codigo;
-- SELECT codalmacenreceptor INTO codAlmaORIG FROM notasalida where codTransaccion = 15 and codSerie=cod and numdocumento=numero;


IF vb_termina = 1 THEN
			LEAVE Recorre_Cursor;
		END IF;

-- notaingreso
 IF EXISTS(SELECT * FROM productoalmacen pal WHERE pal.codProducto = codPro AND pal.codAlmacen = codAlma)
           THEN
                UPDATE productoalmacen
                SET stockactual= stockactual + cant,
                       -- stockdisponible = stockdisponible + cant,
                       soles = soles + valor * cant,
                       totalingresos=totalingresos+cant,
                       totalsolesingresos = totalsolesingresos + valor * cant,
                       ultprecio = valor
                WHERE codProducto = codPro AND codAlmacen = codAlma;
         ELSE
                 INSERT INTO productoalmacen
                  (codProducto,codAlmacen,stockactual/*,stockdisponible*/,valorpromedio,valorpromediosoles,preciopromedio,preciovariable,soles,totalingresos,totalsolesingresos,totalsalidas,totalsolessalidas,ultprecio,codUser,fecharegistro)
                 VALUES
                  (codPro,codAlma,cant/*,cant*/,valor,valors,precio,1, valor* cant,cant,valor * cant,0,0,valor, usuI,NOW());
          END IF;

-- notasalida
IF EXISTS(SELECT * FROM productoalmacen pal WHERE pal.codProducto = codPro AND pal.codAlmacen =codAlmaORIG)
THEN
UPDATE productoalmacen
SET stockactual= stockactual - cant,
-- stockdisponible = stockdisponible - cant,
soles = soles - valorpromedio*cant,
totalsalidas = totalsalidas + cant,
totalsolessalidas = totalsolessalidas + valorpromedio*cant
WHERE codProducto = codPro AND codAlmacen = codAlmaORIG;
END IF;


-- atender requerimiento

IF req<>0
THEN
UPDATE detallerequerimiento
SET cantidadDespachada=cantidadDespachada+cant
WHERE codDetalleRequerimiento=req;
END IF;

END LOOP Recorre_Cursor;
		 
		 CLOSE producto_cursor;


UPDATE notaingreso SET aceptado = 1 where codTransaccion = 15 and codSerie=cod and numdocumento=numero;
UPDATE notasalida SET aceptado = 1, codUsuario=Usu where codTransaccion = 15 and codSerie=cod and numdocumento=numero;

END
;;
DELIMITER ;

