-- ----------------------------
-- Procedure structure for BuscaPrecioProducto
-- ----------------------------
DROP PROCEDURE IF EXISTS `BuscaPrecioProducto`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `BuscaPrecioProducto`(codpro int(11), codalma int(11), mon int(11))
BEGIN
IF(mon=1) -- soles
THEN

IF EXISTS(SELECT * FROM productoalmacen pal WHERE pal.codProducto = codpro AND pal.codAlmacen =codalma)
           THEN
		SELECT IFNULL(valorpromediosoles,0) as preciosoles FROM productoalmacen WHERE codProducto = codpro AND codAlmacen =codalma;

ELSE 

		SELECT IFNULL(preciocompracatalago,0) as preciosoles FROM producto WHERE codProducto = codpro;
END IF;

ELSE -- dolares

SET @tc = (SELECT venta FROM tipocambio WHERE date(fecha) = date(NOW()));

IF EXISTS(SELECT * FROM productoalmacen pal WHERE pal.codProducto = codpro AND pal.codAlmacen =codalma)
           THEN
		SELECT IFNULL(valorpromedio,0) as preciodolares FROM productoalmacen WHERE codProducto = codpro AND codAlmacen =codalma;

ELSE 

		SELECT ROUND(IFNULL(preciocompracatalago,0)*@tc,4) as preciodolares FROM producto WHERE codProducto = codpro;
END IF;

END IF;

END
;;
DELIMITER ;

