DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `BuscarClienteNota`(filtro varchar(11),tipo int(1))
BEGIN

IF tipo = 1  THEN

	SELECT 	codCliente,IFNULL(codigopersonalizado,""),IFNULL(razonsocial,""),IFNULL(ruc,""),IFNULL(dni,""), IFNULL(direccionlegal,""), IFNULL(direccionentrega,""),1,IFNULL(formapago,0),
					IFNULL(codListaPrecio,0), IFNULL(descuento,0), IFNULL(codVendedor,0), IFNULL(lineacredito,0), 
          IFNULL(lineacreditodisponible,0), IFNULL(IFNULL(ruc,dni),0), (lineacredito-lineacreditodisponible)AS linea_uso
	FROM 		cliente
	WHERE 	dni = filtro AND estado = 1 AND habilitado = 1;

ELSEIF 	tipo = 2 THEN

	SELECT 	codCliente,IFNULL(codigopersonalizado,""),IFNULL(razonsocial,""),IFNULL(ruc,""),IFNULL(IFNULL(ruc,dni),0),IFNULL(direccionlegal,""), IFNULL(direccionentrega,""), 2,IFNULL(formapago,0),
					IFNULL(codListaPrecio,0), IFNULL(descuento,0), IFNULL(codVendedor,0), IFNULL(lineacredito,0), 
          IFNULL(lineacreditodisponible,0),  IFNULL(IFNULL(ruc,dni),0), (lineacredito-lineacreditodisponible)AS linea_uso
	FROM 		cliente
	WHERE 	ruc = filtro AND estado = 1 AND habilitado = 1;

ELSEIF 	tipo = 3 THEN

	SELECT 	codCliente,IFNULL(codigopersonalizado,""),IFNULL(razonsocial,""),IFNULL(ruc,""),IFNULL(IFNULL(ruc,dni),0),IFNULL(direccionlegal,""), IFNULL(direccionentrega,""), 3,IFNULL(formapago,0),
					IFNULL(codListaPrecio,0), IFNULL(descuento,0), IFNULL(codVendedor,0), IFNULL(lineacredito,0), 
          IFNULL(lineacreditodisponible,0),  IFNULL(IFNULL(ruc,dni),0), (lineacredito-lineacreditodisponible)AS linea_uso
	FROM 		cliente
	WHERE 	codigopersonalizado = filtro AND estado = 1 AND habilitado = 1;

END IF;
END
;;
DELIMITER ;

-- ----------------------------
