-- ----------------------------
-- Procedure structure for CargaConsolidado
-- ----------------------------
DROP PROCEDURE IF EXISTS `CargaConsolidado`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `CargaConsolidado`(alma int(11), usu int(11))
BEGIN
select dt.Coddetalle as codDetalle, dt.codProducto, p.referencia, p.descripcion as producto,
u.codUnidadMedida,CONCAT(1) as moneda, u.descripcion as unidad,'0' as serielote, 
 IFNULL((select stockactual from productoalmacen where codAlmacen=alma and codProducto=p.codProducto  GROUP BY codProducto ) ,0) as StockActual,
SUM(dt.cantidad) as cantidad,sum(dt.cantidad) as cantidadpendiente,0.00 as preciounitario, 0.00 as subtotal, 0.00 as descuento1, 0.00 as descuento2, 0.00 as descuento3, 0.00 as montodscto, 0.00 as valorventa, 
0.00 as vvconflete, 0.00 as igv, 0.00 as flete, 0.00 as importe, 0.00 as pvconflete, 0.00 as precioreal, 0.00 as valoreal , NOW() as fecharegistro, CONCAT(0)  as codUser,
IFNULL((select stockpendiente from productoalmacen where codAlmacen=alma and codProducto=p.codProducto  GROUP BY codProducto ),0) as PorLlegar,
 IFNULL((select sum(cantidad) from detallerequerimiento where cantidad>cantidaddespachada and estado=1 and anulado=0 and vigente=1 and codProducto=p.codProducto and CodOrdenCompra is not null GROUP BY codProducto),0) as PorAtender,
IFNULL((( IFNULL((select stockpendiente from productoalmacen where codAlmacen=alma and codProducto=p.codProducto  GROUP BY codProducto ),0)+
 IFNULL((select stockactual from productoalmacen where codAlmacen=alma and codProducto=p.codProducto  GROUP BY codProducto ) ,0))
-(SUM(dt.cantidad)+
 IFNULL((select sum(cantidad) from detallerequerimiento where cantidad>cantidaddespachada and estado=1 and anulado=0 and vigente=1 and codProducto=p.codProducto and CodOrdenCompra is not null  GROUP BY codProducto),0))
),0) as PorCompletar
 from detalleconsolidado dt 
inner JOIN producto p on dt.codProducto= p.codProducto
INNER JOIN unidadmedida u on p.codUnidadMedida= u.codUnidadMedida
WHERE dt.codalmacen=alma and dt.codusuario=usu
group by dt.codProducto;

END
;;
DELIMITER ;

