-- ----------------------------
-- Procedure structure for CargaDetalleConsolidado
-- ----------------------------
DROP PROCEDURE IF EXISTS `CargaDetalleConsolidado`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `CargaDetalleConsolidado`(codalma int(11), codprov int(11))
BEGIN
IF codprov = 0
THEN
SELECT dt.codDetalleRequerimiento as coddetalle, date(dt.fecharegistro) as fecharequerida,CONCAT(LEFT(td.descripcion,3),' - ',s.serie,' - ', r.numeracion)as numeracion,
(SELECT a1.nombre from almacen a1 where a1.codAlmacen=r.codalmacen) as descripcion,
dt.codProducto, p.referencia, p.descripcion as producto,IFNULL(pv.razonsocial,"") as proveedor,
u.codUnidadMedida, u.descripcion as unidad, dt.cantidad,
IFNULL((SELECT p1.stockactual from productoalmacen p1 where p1.codalmacen=r.codalmacen and p1.codproducto=dt.codProducto),0) as stockactual,
IFNULL((SELECT paa.stockactual from productoalmacen paa where paa.codproducto=dt.codProducto and paa.codAlmacen=codalma/*(
SELECT al1.codAlmacen FROM almacen al1 WHERE al1.estado_principal=1)*/),0) as stockgeneral
FROM detallerequerimiento dt
inner join requerimiento r on r.codRequerimiento=dt.codRequerimiento
INNER JOIN producto p on dt.codProducto= p.codProducto
INNER JOIN unidadmedida u on dt.unidad=u.codUnidadMedida
INNER JOIN almacen al on al.codAlmacen=r.almacendestino
LEFT JOIN productoalmacen pa on pa.codProducto=dt.codProducto and pa.codAlmacen=al.codalmacen
INNER JOIN tipodocumento td on td.codTipoDocumento=r.codTipoDocumento
INNER JOIN serie s on s.codserie=r.codserie
LEFT OUTER JOIN productosproveedor pp ON pp.codProducto = p.codProducto
LEFT OUTER JOIN proveedor pv ON pv.codProveedor = pp.codProveedor
where dt.vigente=1 and dt.anulado=0 and dt.codOrdenCompra is NULL
and r.almacendestino=codalma and dt.estado=1 ;

ELSE
select dt.codDetalleRequerimiento as codDetalle, date(dt.fecharegistro) as fecharequerida,
CONCAT(LEFT(td.descripcion,3),' - ',s.serie,' - ', r.numeracion)as numeracion,
(SELECT a1.nombre from almacen a1 where a1.codAlmacen=r.codalmacen) as descripcion,
dt.codProducto, pr.referencia, pr.descripcion as producto,IFNULL(pro.razonsocial,"") as proveedor,
u.codUnidadMedida, u.descripcion as unidad, dt.cantidad,
IFNULL((SELECT p1.stockactual from productoalmacen p1 where p1.codalmacen=r.codalmacen and p1.codproducto=dt.codProducto),0) as stockactual,
IFNULL((SELECT paa.stockactual from productoalmacen paa where paa.codproducto=dt.codProducto and paa.codAlmacen=codalma/*(
SELECT al1.codAlmacen FROM almacen al1 WHERE al1.estado_principal=1)*/),0) as stockgeneral
from producto pr
INNER JOIN detallerequerimiento dt on dt.codProducto=pr.codProducto
inner join requerimiento r on r.codRequerimiento=dt.codRequerimiento
LEFT OUTER JOIN productosproveedor pp on pp.codProducto = pr.codProducto
LEFT OUTER JOIN proveedor pro on pro.codProveedor = pp.codProveedor
INNER JOIN unidadmedida u on dt.unidad=u.codUnidadMedida
inner JOIN almacen al on al.codAlmacen=r.almacendestino
LEFT JOIN productoalmacen pa on pa.codProducto=dt.codProducto and pa.codAlmacen=al.codalmacen
INNER JOIN tipodocumento td on td.codTipoDocumento=r.codTipoDocumento
INNER JOIN serie s on s.codserie=r.codserie
where pro.codProveedor = codprov and pr.estado = 1 AND pr.codTipoArticulo = 1 and al.codAlmacen=codalma AND 
dt.vigente=1 and dt.anulado=0 and r.almacendestino=codalma and  dt.codOrdenCompra is NULL and dt.estado=1 

UNION

SELECT dt.codDetalleRequerimiento as codDetalle, date(dt.fecharegistro) as fecharequerida,
CONCAT(LEFT(td.descripcion,3),' - ',s.serie,' - ', r.numeracion)as numeracion,
(SELECT a1.nombre from almacen a1 where a1.codAlmacen=r.codalmacen) as descripcion,
dt.codProducto, p.referencia, p.descripcion as producto,"" as proveedor,
u.codUnidadMedida, u.descripcion as unidad, dt.cantidad,IFNULL(pa.stockactual,0) as stockactual, 
IFNULL((SELECT paa.stockactual from productoalmacen paa where paa.codproducto=dt.codProducto and paa.codAlmacen=(
SELECT al1.codAlmacen FROM almacen al1 WHERE al1.estado_principal=1)),0) as stockgeneral
FROM detallerequerimiento dt
inner join requerimiento r on r.codRequerimiento=dt.codRequerimiento
INNER JOIN producto p on dt.codProducto= p.codProducto
INNER JOIN unidadmedida u on dt.unidad=u.codUnidadMedida
INNER JOIN almacen al on al.codAlmacen=r.almacendestino
LEFT JOIN productoalmacen pa on pa.codProducto=dt.codProducto and pa.codAlmacen=al.codalmacen
INNER JOIN tipodocumento td on td.codTipoDocumento=r.codTipoDocumento
INNER JOIN serie s on s.codserie=r.codserie
where dt.vigente=1 and dt.anulado=0 and dt.estado=1 and dt.codOrdenCompra is NULL
and r.almacendestino=codalma and dt.codProducto not in(
select pp1.codProducto from productosproveedor pp1 where pp1.codProducto=p.codProducto
);


END IF;
END
;;
DELIMITER ;

