         dt.preciounitario, dt.subtotal, dt.descuento1, dt.descuento2, dt.descuento3, dt.montodscto, 
        (dt.importe - dt.igv) as valorventa, dt.igv, dt.importe,dt.valoreal, dt.precioreal, 
        IFNULL((SELECT pa.stockdisponible FROM productoalmacen pa WHERE pa.codProducto=p.codProducto AND pa.codAlmacen=codalma),0)as stockdisponible, 
        p.maxPorcDescto, dt.codUser, dt.fecharegistro, dt.codFacturaV as codventa

