DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `CargaLoteProduccionXEstado`(esta int, fecha1 date, fecha2 date)
BEGIN
IF esta = 2 THEN
	SELECT lp.codLoteProduccion,lp.codEspecie,lp.codAutorizado,lp.numeroorden,DATE_FORMAT(lp.fecha,'%d-%m-%Y') AS    fechaproduccion,
  DATE_FORMAT(lp.fechafin,'%d-%m-%Y') AS fechafin, DATE_FORMAT(lp.horaInicio,'%H:%i')   as horainicio, DATE_FORMAT(lp.horaFinal,'%H:%i') as horafin,lp.observacion,
	IF(lp.estadoLote=1,'ACTIVO','CERRADO') as estadoLote,
	lp.estado, lp.codUser,   DATE_FORMAT(lp.fecharegistro,'%d-%m-%Y') AS  fecharegistro, es.nombre as especie, au.nombre as responsable,
  DATE_FORMAT(fecha1,'%d-%m-%Y') AS fecha1,DATE_FORMAT(fecha2,'%d-%m-%Y') AS fecha2, 
  lp.totalMP as  totalMP, lp.totalPT as totalpt,lp.rendimiento

	FROM loteproduccion lp
	INNER JOIN especie es ON lp.codEspecie=es.codEspecie
	INNER JOIN autorizado au ON lp.codAutorizado = au.codAutorizado
	where lp.estado=1 AND DATE(lp.fecha) BETWEEN DATE(fecha1) AND DATE(fecha2)
	ORDER BY lp.codLoteProduccion;
ELSE
	SELECT lp.codLoteProduccion,lp.codEspecie,lp.codAutorizado,lp.numeroorden,DATE_FORMAT(lp.fecha,'%d-%m-%Y') AS    fechaproduccion,
	DATE_FORMAT(lp.fechafin,'%d-%m-%Y') AS fechafin,DATE_FORMAT(lp.horaInicio,'%H:%i')   as horainicio, DATE_FORMAT(lp.horaFinal,'%H:%i') as horafin,lp.observacion,
	IF(lp.estadoLote=1,'ACTIVO','CERRADO') as estadoLote,
	lp.estado, lp.codUser,DATE_FORMAT(lp.fecharegistro,'%d-%m-%Y') AS  fecharegistro, es.nombre as especie, au.nombre as responsable,
   DATE_FORMAT(fecha1,'%d-%m-%Y') AS fecha1,DATE_FORMAT(fecha2,'%d-%m-%Y') AS fecha2,
  lp.totalMP as  totalMP, lp.totalPT as totalpt,lp.rendimiento
	
  FROM loteproduccion lp
	INNER JOIN especie es ON lp.codEspecie=es.codEspecie
	INNER JOIN autorizado au ON lp.codAutorizado = au.codAutorizado
	where lp.estadoLote=esta AND lp.estado=1 AND DATE(lp.fecha) BETWEEN DATE(fecha1) AND DATE(fecha2)
	ORDER BY lp.codLoteProduccion;

END IF;
END
;;
DELIMITER ;

