DROP PROCEDURE IF EXISTS `consultadinamicastock`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `consultadinamicastock`()
BEGIN
	
DECLARE codalma INT(11);
DECLARE descrip_alma VARCHAR(100);
DECLARE last_fila INT(11) DEFAULT 0;

DECLARE c_codalmacen CURSOR FOR SELECT codalmacen, nombre FROM almacen WHERE estado = 1;

DECLARE CONTINUE HANDLER FOR NOT FOUND SET last_fila = 1;

SET @consql := ('SELECT p.codProducto AS codProducto, p.referencia, p.descripcion as producto,
IFNULL((SELECT p1.razonsocial FROM productosproveedor pp INNER JOIN proveedor p1 on pp.codProveedor=p1.codProveedor WHERE pp.codProducto=p.codProducto LIMIT 1),"") as proveedor');

OPEN c_codalmacen;
  c1_loop:LOOP
     FETCH c_codalmacen INTO codalma, descrip_alma;
     IF last_fila=1 THEN 
        LEAVE c1_loop;
     END IF; 
			
			SET @consql :=( CONCAT(@consql,',IFNULL(
     (SELECT pa.stockactual FROM productoalmacen pa WHERE pa.codProducto=p.codProducto AND pa.codAlmacen=',codalma,'),0) AS ',REPLACE(descrip_alma," ",""),' '));		
	
END LOOP;

SET @consql :=( CONCAT(@consql,',IFNULL((SELECT SUM(pa1.stockactual) from productoalmacen pa1 WHERE pa1.codProducto=p.codProducto GROUP BY pa1.codProducto),0)','as TOTAL FROM producto p;'));

 PREPARE stmt FROM @consql;
 EXECUTE stmt;
-- select @consql;


CLOSE c_codalmacen;
END
;;
DELIMITER ;

