-- ----------------------------
-- Procedure structure for CorrigeFechasCanceladas
-- ----------------------------
DROP PROCEDURE IF EXISTS `CorrigeFechasCanceladas`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `CorrigeFechasCanceladas`()
BEGIN
	
DECLARE v_finished 		INTEGER DEFAULT 0;
DECLARE v_codigo 			INTEGER DEFAULT 0;

BEGIN
DECLARE codnota_cursor CURSOR FOR 
SELECT  nt.codNotaSalida
FROM  notasalida nt INNER JOIN pago p ON nt.codNotaSalida = p.codNota AND p.ingresoegreso = 1 AND p.estado = 1 AND nt.cancelado = 1
GROUP BY nt.codNotaSalida ;

DECLARE CONTINUE HANDLER 
FOR NOT FOUND SET v_finished = 1;

OPEN codnota_cursor;

update_nota: LOOP
		 
			FETCH codnota_cursor INTO v_codigo;

			IF v_finished = 1 THEN 
					LEAVE update_nota;
			END IF;

			UPDATE notasalida SET fechacancelado = (SELECT MAX(pp.fechapago) FROM pago pp WHERE pp.codNota = v_codigo) WHERE codNotaSalida = v_codigo;

END LOOP update_nota;

CLOSE codnota_cursor;
END;


END
;;
DELIMITER ;

