BEGIN
declare sal decimal(10,3);
set sal= (select IFNULL(SUM(ingreso),0) -  IFNULL(SUM(egreso),0)  from ctactemovimientos where codCuentaCorriente = codcuen and estado = 1);
#1 soles , 2 dolares
#0 egresos , 1 ingresos
if (tipo = 0) THEN

INSERT INTO ctactemovimientos (codCuentaCorriente,codAlmacen,NumTransaccion,descripcion,moneda,tcventa,tccompra,egreso,
saldo,codUser,fechaMovimiento,fechaRegistro,codtipopagoservicio)
values (codcuen,codalma,codtrans,descrip,mone,tcve,tccom,ingresoegreso,(sal-ingresoegreso),codu,fechamov,NOW(),codtipo);

elseif(tipo = 1) THEN
INSERT INTO ctactemovimientos (codCuentaCorriente,codAlmacen,NumTransaccion,descripcion,moneda,tcventa,tccompra,ingreso,
saldo,codUser,fechaMovimiento,fechaRegistro,codtipopagoservicio)
values (codcuen,codalma,codtrans,descrip,mone,tcve,tccom,ingresoegreso,(sal+ingresoegreso),codu,fechamov,NOW(),codtipo);
end IF;
SET newid = LAST_INSERT_ID();
END
;;
