DROP PROCEDURE IF EXISTS `ListaDetalleRendiciones`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `ListaDetalleRendiciones`(codRend int(11))
BEGIN
#Routine body goes here...
SELECT 	d.codDetalleRendicion, CONCAT("C",LPAD(c.codCajaChica,7,0)) codPersonalizado, c.concepto, c.fecha, 
				c.NumDocumento, c.numGuia, c.numRecLiquidacion, IFNULL(t.codTipoPagoCaja,0) as codTipoPagoCaja,
				t.descripcion as TipoPagoCaja, (CASE cargadescarga WHEN 1 THEN 'CARGA' WHEN 2 THEN 'DESCARGA' 
				ELSE '' END) as cargadescarga, (CASE c.tipo WHEN 1 then 'INGRESO' WHEN 2 THEN 'EGRESO' END) as 
				TipoMovimiento, IF(c.toneladas>0,c.toneladas, NULL) AS toneladas, c.monto, d.comentario, d.estatus,
				(CASE d.estatus WHEN 0 THEN 'RECHAZADA' WHEN 1 THEN 'PENDIENTE' WHEN 2 THEN 'APROBADA' END) as 'status'

FROM rendicion as r 
INNER JOIN detallerendicion as d ON r.codRendicion = d.codRendicion
INNER JOIN cajachica as c ON c.codCajaChica = d.codCajaChica
LEFT JOIN tipopagocaja as t ON t.codTipoPagoCaja = c.codTipoPagoCaja

WHERE r.codRendicion = codRend AND r.estado = 1 AND d.estado=1

ORDER BY c.codCajaChica;

END
;;
