DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ListaGuiaRemisiones`(codalma int(11))
BEGIN

SELECT LPAD(gr.codGuiaRemision,6,'0') as codGuiaRemision, CONCAT(td.sigla,' - ',s.serie,' - ',gr.numdocumento) AS numdoc,
     IF(IFNULL(cl.codCliente,0)!=0,(SELECT CONCAT(t.sigla,' - ',se.serie,' - ',f.numDocumento)
        FROM tipodocumento t INNER JOIN factura_venta f ON t.codTipoDocumento=f.codTipoDocumento INNER JOIN serie se ON f.codSerie=se.codSerie
        WHERE f.codFacturaV=gr.codFactura),(SELECT CONCAT(t.sigla,' - ',se.serie,' - ',f.numdocumento)
