              WHERE f.codNotaSalida=gr.codFactura AND f.codTransaccion=15))as factura, DATE_FORMAT(gr.fechatraslado,'%d-%m-%Y')as fecha,
     IF(IFNULL(cl.codCliente,0)=0,(SELECT al.nombre FROM almacen al WHERE al.codAlmacen = (SELECT n.codalmacenreceptor FROM notasalida n 
        WHERE n.codNotaSalida=gr.codFactura)),cl.razonsocial)as cliente, CONCAT(u.nombre," ",u.apellido) as responsable, 
     DATE_FORMAT(gr.fechaemision,'%d-%m-%Y')as fechaemision, gr.facturado, 
    (CASE gr.facturado WHEN 1 THEN "FACTURADO" WHEN 0 THEN "SIN FACTURAR" END)AS estadofacturado
FROM guiaremision gr INNER JOIN usuario u ON gr.codUser=u.codUsuario
     INNER JOIN serie s ON s.codSerie=gr.codserie
     INNER JOIN tipodocumento td ON td.codTipoDocumento=gr.codDocumento
     LEFT OUTER JOIN cliente cl ON cl.codCliente=gr.codCliente
WHERE gr.estado=1 AND gr.codAlmacen=codalma
ORDER BY gr.codGuiaRemision desc;
END
;;
DELIMITER ;

