DROP PROCEDURE IF EXISTS `ListaNotas`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ListaNotas`(criterio int(1), codalma int(11), fechaini datetime, fechafin datetime)
BEGIN

IF criterio = 0 THEN

		SELECT 		"NI" as tipo,LPAD(n.codNotaIngreso,11,'0') as codNota, tr.sigla as movimiento, DATE(n.fechaingreso) as fecha, 
							pr.ruc as proveedor,"" as cliente, td.sigla as documento, n.numdocumento, m.descripcion AS moneda, n.bruto, 
							n.montodscto,n.igv,(n.bruto - n.montodscto + n.igv  ) AS  total,fp.descripcion as formapago, n.fechapago, (SELECT CASE n.anulado WHEN 0 THEN 
							"ACTIVO" WHEN 1 THEN "ANULADO" END) AS anulado

		FROM 			notaingreso n INNER JOIN transaccion tr on tr.codTransaccion = n.codTransaccion
		LEFT OUTER JOIN proveedor pr on pr.codProveedor = n.codProveedor
		LEFT OUTER JOIN tipodocumento td on td.codTipoDocumento = n.codTipoDocumento
		LEFT OUTER JOIN formapago fp ON fp.codFormaPago = n.formapago
		LEFT OUTER JOIN moneda m on m.codMoneda=n.moneda
		WHERE 		n.codAlmacen = codalma AND n.estado = 1 AND date(n.fechaingreso) BETWEEN fechaini AND fechafin
		ORDER BY 	n.fechaingreso DESC, n.codNotaIngreso DESC;



ELSEIF criterio = 1 THEN

		SELECT "NS" as tipo,LPAD(n.codNotaSalida,11,'0') as codNota, tr.sigla as movimiento, DATE(n.fechasalida) as fecha,
    IF(IFNULL(documentoreferencia,'')='','',
      (SELECT p.ruc FROM notaingreso n INNER JOIN proveedor p ON n.codProveedor=p.codProveedor 
       WHERE n.codNotaIngreso=documentoreferencia))as proveedor -- "" as proveedor
   ,IFNULL(IFNULL(cl.ruc,cl.dni),"")as cliente,td.sigla as documento, n.numdocumento, 
  (SELECT CASE n.moneda WHEN 1 THEN "SOLES" WHEN 2 THEN "DOLARES" END)AS moneda, n.bruto, n.montodscto, n.igv,(n.bruto - n.montodscto + n.igv  ) AS  total ,
  fp.descripcion as formapago, n.fechapago, (SELECT CASE n.anulado WHEN 0 THEN "ACTIVO" WHEN 1 THEN "ANULADO" END) AS anulado,
  IFNULL(n.documentoreferencia,0)AS documentoreferencia

		FROM notasalida n INNER JOIN transaccion tr on tr.codTransaccion = n.codTransaccion
		LEFT OUTER JOIN cliente cl on cl.codCliente = n.codCliente
		LEFT OUTER JOIN tipodocumento td on td.codTipoDocumento = n.codTipoDocumento
		LEFT OUTER JOIN formapago fp ON fp.codFormaPago = n.formapago
		WHERE n.codAlmacen = codalma AND n.estado = 1 AND date(n.fechasalida) BETWEEN fechaini AND fechafin
		ORDER BY n.fechasalida DESC, n.codNotaSalida DESC;



ELSEIF criterio = 2 THEN
		SELECT 		"NI" as tipo,LPAD(n.codNotaIngreso,11,'0') as codNota, tr.sigla as movimiento, DATE(n.fechaingreso) as fecha, 
							pr.ruc as proveedor,"" as cliente, td.sigla as documento, n.numdocumento, (SELECT CASE n.moneda WHEN 1 THEN 
							"SOLES" WHEN 2 THEN "DOLARES" END) AS moneda, n.bruto, n.montodscto, n.igv, (n.bruto - n.montodscto + n.igv  ) AS  total, fp.descripcion as formapago, 
							n.fechapago, (SELECT CASE n.anulado WHEN 0 THEN "ACTIVO" WHEN 1 THEN "ANULADO" END) AS anulado

		FROM 			notaingreso n INNER JOIN transaccion tr on tr.codTransaccion = n.codTransaccion
		LEFT OUTER JOIN proveedor pr on pr.codProveedor = n.codProveedor
		LEFT OUTER JOIN tipodocumento td on td.codTipoDocumento = n.codTipoDocumento
		LEFT OUTER JOIN formapago fp ON fp.codFormaPago = n.formapago
		WHERE 		n.codAlmacen = codalma AND n.estado = 1 AND date(n.fechaingreso) BETWEEN fechaini AND fechafin

		UNION

		SELECT 		"NS" as tipo,LPAD(n.codNotaSalida,11,'0') as codNota, tr.sigla as movimiento, DATE(n.fechasalida) as fecha,
							"" as proveedor,IF(cl.dni="",cl.ruc,cl.dni) as cliente,td.sigla as documento, n.numdocumento, (SELECT CASE n.moneda 
							WHEN 1 THEN "SOLES" WHEN 2 THEN "DOLARES" END) AS moneda, n.bruto, n.montodscto, n.igv, (n.bruto - n.montodscto + n.igv  ) AS  total, fp.descripcion 
							as formapago, n.fechapago, (SELECT CASE n.anulado WHEN 0 THEN "ACTIVO" WHEN 1 THEN "ANULADO" END) AS anulado

		FROM 			notasalida n 	INNER JOIN transaccion tr on tr.codTransaccion = n.codTransaccion
		LEFT OUTER JOIN cliente cl on cl.codCliente = n.codCliente
		LEFT OUTER JOIN tipodocumento td on td.codTipoDocumento = n.codTipoDocumento
		LEFT OUTER JOIN formapago fp ON fp.codFormaPago = n.formapago
		WHERE 		n.codAlmacen = codalma AND n.estado = 1 AND date(n.fechasalida) BETWEEN fechaini AND fechafin
		ORDER BY 	fecha DESC, codNota DESC;
END IF;


END
;;
DELIMITER ;

