BEGIN
	IF criterio = 0 THEN
		SELECT 		"NI" as tipo,LPAD(n.codFactura,11,'0') as codFactura, tr.sigla as movimiento, n.fechaingreso as fecha, pr.ruc as proveedor,"" as cliente, 
