		FROM 			notasalida n 	INNER JOIN transaccion tr on tr.codTransaccion = n.codTransaccion
		LEFT OUTER JOIN cliente cl on cl.codCliente = n.codCliente
		LEFT OUTER JOIN tipodocumento td on td.codTipoDocumento = n.codTipoDocumento
		LEFT OUTER JOIN formapago fp ON fp.codFormaPago = n.formapago
		WHERE 		n.codAlmacen = codalma AND n.estado = 1 AND date(n.fechasalida) BETWEEN fechaini AND fechafin and n.codTipoDocumento !=19
