		FROM 			notaingreso n INNER JOIN transaccion tr on tr.codTransaccion = n.codTransaccion
		LEFT OUTER JOIN proveedor pr on pr.codProveedor = n.codProveedor
		LEFT OUTER JOIN tipodocumento td on td.codTipoDocumento = n.codTipoDocumento
		LEFT OUTER JOIN formapago fp ON fp.codFormaPago = n.formapago
		WHERE 		n.codAlmacen = codalma AND n.estado = 1 AND date(n.fechaingreso) BETWEEN fechaini AND fechafin and n.codTipoDocumento !=19
		UNION
		SELECT 		"NS" as tipo,LPAD(n.codNotaSalida,11,'0') as codNota, tr.sigla as movimiento, n.fechasalida as fecha,"" as proveedor,cl.codigopersonalizado as cliente,
							td.sigla as documento, n.numdocumento, (SELECT CASE n.moneda WHEN 0 THEN "SOLES" WHEN 1 THEN "DOLARES" END) AS moneda, n.bruto, n.montodscto,n.igv, 
							n.total,fp.descripcion as formapago, n.fechapago, (SELECT CASE n.anulado WHEN 0 THEN "ACTIVO" WHEN 1 THEN "ANULADO" END) AS anulado
		FROM 			notasalida n 	INNER JOIN transaccion tr on tr.codTransaccion = n.codTransaccion
		LEFT OUTER JOIN cliente cl on cl.codCliente = n.codCliente
		LEFT OUTER JOIN tipodocumento td on td.codTipoDocumento = n.codTipoDocumento
		LEFT OUTER JOIN formapago fp ON fp.codFormaPago = n.formapago
