-- ----------------------------
-- Procedure structure for ListaRendicionesSucursal
-- ----------------------------
DROP PROCEDURE IF EXISTS `ListaRendicionesSucursal`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `ListaRendicionesSucursal`(codSucur int(11))
BEGIN
#Routine body goes here...
SELECT 	r.codRendicion, r.numDocumento, r.montoRendido, r.montoAprobado, r.montoDesaprobado, 
				r.fechaRendicion, observacion, (CASE r.estatus WHEN '1' THEN 'VIGENTE' WHEN '2' THEN 
				'RECHAZADO' WHEN '3' THEN 'APROBADO' WHEN '4' THEN 'LIQUIDADO' END) as estatus, 
				(SELECT 	COUNT(*) FROM detallerendicion WHERE codRendicion = r.codRendicion AND estado=1) as TotalRendiciones,
				(SELECT COUNT(*) FROM detallerendicion as de WHERE de.codRendicion = r.codRendicion AND de.estatus = 2 AND de.estado=1) as Aprobadas,
				(SELECT COUNT(*) FROM detallerendicion as dr WHERE dr.codRendicion = r.codRendicion AND dr.estatus = 0 AND dr.estado=1) as  Desaprobadas
 

FROM rendicion as r
INNER JOIN detallerendicion as d ON r.codRendicion = d.codRendicion
INNER JOIN sucursal as s ON s.codSucursal = r.codSucursal
WHERE r.estado = 1 AND r.estatus = 1 AND r.codSucursal = codSucur

GROUP BY r.codRendicion;

END
;;
DELIMITER ;

