	#Routine body goes here...

SELECT  ni.codNotaIngreso aS numero,ni.numdocumento AS numdocumento,ni.fechaingreso AS fecha,lp.codLoteProduccion AS  lote,
esp.nombre as Especie,ROUND(SUM(dni.cantidad),2) as CantidadTotal,
