DROP PROCEDURE IF EXISTS `ListaTransacciones`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ListaTransacciones`(caso int(1))
BEGIN
#Routine body goes here...
IF caso = 2 THEN
	SELECT *, (SELECT case tipo when 0 then "INGRESO" when 1 then "SALIDA" END)as tipotext
	FROM transaccion
	WHERE estado = 1
	ORDER BY tipotext, descripcion ;
ELSEIF caso = 0 THEN
	SELECT *
	FROM transaccion
	WHERE tipo = 0 AND estado = 1
	ORDER BY descripcion ;
ELSEIF caso = 1 THEN
	SELECT *
	FROM transaccion
	WHERE tipo = 1 AND estado = 1
	ORDER BY descripcion ;
ELSEIF caso = 3 THEN
	SELECT * 
	FROM transaccion 
	WHERE tipo = 1 AND estado = 1 and descripcion not like '%directa'
	ORDER BY descripcion ;
END IF;
END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for ListaTransferenciasHistorial
-- ----------------------------
DROP PROCEDURE IF EXISTS `ListaTransferenciasHistorial`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `ListaTransferenciasHistorial`(codalma int(11))
BEGIN
SELECT ni.codNotaIngreso,s.codSerie as serie,ni.numdocumento,CONCAT(s.serie," - ",ni.numdocumento) as documento,
DATE(ni.fechaingreso) as fecha, CONCAT(u.nombre," ",u.apellido) as responsable,ni.comentario,ni.codAlmacen,
(SELECT CASE ni.codTransaccion
	when 15 then "DIRECTA" 
	when 17 Then "POR DEVOLUCION" 
END) as transferencia
FROM 		notaingreso ni
INNER JOIN usuario u on u.codUsuario = ni.codUsuario
inner join almacen al on al.codAlmacen=ni.codAlmacen
INNER JOIN tipodocumento td on td.codTipoDocumento=ni.codTipoDocumento
LEFT OUTER JOIN serie s on s.codSerie=ni.codSerie
WHERE ni.codAlmacen=codalma and  (ni.codTransaccion=15 or ni.codTransaccion=17) and ni.anulado=0
ORDER BY  CONCAT(s.serie," - ",ni.numdocumento);
END
;;
DELIMITER ;

