CREATE DEFINER=`root`@`localhost` PROCEDURE `MuestraCliente`(codcli  int(11))
BEGIN
	SELECT DISTINCT(cli.codCliente) AS codCliente, 
         IFNULL(cli.codListaPrecio,0), 
         IFNULL(cli.codigopersonalizado,""), 
         IFNULL(cli.nombre,""), IFNULL(cli.dni,""), IFNULL(cli.ruc,""), IFNULL(cli.razonsocial,""), IFNULL(cli.direccionlegal,""), IFNULL(cli.direccionentrega,""),
				 IFNULL(cli.telefono,""), IFNULL(cli.email,""), IFNULL(cli.web,""), IFNULL(cli.pais,""), IFNULL(cli.departamento,""), IFNULL(cli.provincia,""),
				 IFNULL(cli.distrito,""), IFNULL(cli.zona,0), IFNULL(cli.descuento,0),  IFNULL(cli.formapago,0), IFNULL(cli.moneda,-1), IFNULL(cli.lineacredito,0),IFNULL(cli.lineacreditodisponible,0),
				 IFNULL(cli.comentario,""), IFNULL(cli.banco,""), IFNULL(cli.ctacte,""), IFNULL(cli.contacto,""), IFNULL(cli.telefonocontacto,""),
				 cli.estado, cli.codUser, cli.fecharegistro,IFNULL(cli.codVendedor,0),cli.habilitado, IFNULL(IFNULL(cli.ruc,cli.dni),0) AS ruc_dni, IFNULL((cli.lineacredito-cli.lineacreditodisponible),0)as linea_uso,
         cli.facturasvencidas
	FROM cliente cli 
  WHERE cli.codCliente = codcli;
END
;;
