DROP PROCEDURE IF EXISTS `MuestraCotizacion`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `MuestraCotizacion`(codcotizacion  int(11), codalma int(11))
BEGIN
SELECT 	LPAD(pv.codCotizacion,11,'0')AS codCotizacion, pv.codAlmacen
,	pv.tipocliente, pv.codCliente, IFNULL(cl.dni,"") ,IFNULL(cl.ruc,""),IFNULL(cl.codigopersonalizado,""), IFNULL(cl.razonsocial,""),
  IFNULL(cl.nombre,""),IFNULL(cl.direccionlegal,""), pv.moneda, pv.tipocambio, pv.fechacotizacion, IFNULL(pv.comentario,"") , pv.bruto, pv.montodscto, 
  pv.igv, pv.total, pv.estado, pv.formapago, pv.fechapago
, pv.codUsuario, pv.fecharegistro, pv.codAutorizado, IFNULL(au.nombre,""), pv.vigencia, pv.fechavigencia, pv.codListaPrecio,pv.codTipoDocumento,
  td.sigla AS siglaDocumento, pv.codSucursal, pv.pendiente, pv.anulado, cl.lineacredito, cl.lineacreditodisponible, 
  (cl.lineacredito - cl.lineacreditodisponible)AS linea_uso
FROM 		cotizacion pv
LEFT OUTER JOIN cliente cl on pv.codCliente = cl.codCliente
LEFT OUTER JOIN autorizado au ON au.codAutorizado = pv.codAutorizado
LEFT OUTER JOIN tipodocumento td ON td.codTipoDocumento=pv.codTipoDocumento
LEFT OUTER JOIN moneda mo ON mo.codMoneda = pv.moneda
WHERE 	pv.codCotizacion = codcotizacion AND pv.codAlmacen=codalma;
END
;;
DELIMITER ;

