       um.codUnidadMedida, um.descripcion AS unidad, p.codControlStock as serielote,
       (SELECT SUM(d.cantidad) FROM detallenotasalida d 
        WHERE d.preciounitario=dt.preciounitario AND d.codProducto=p.codProducto)AS cantidad, 
       dt.preciounitario, dt.subtotal, dt.descuento1,dt.descuento2,dt.descuento3,dt.montodscto as montodscto, 
       (dt.importe-dt.igv) AS valorventa, dt.igv, dt.importe, 
       IFNULL((SELECT pa.stockdisponible FROM  productoalmacen pa 
							 WHERE p.codProducto=pa.codProducto AND pa.codAlmacen=codalma),0)  AS stockdisponible,
        dt.precioreal, dt.valoreal, dt.codUser, dt.fecharegistro

  FROM detallenotasalida dt INNER JOIN producto p ON p.codProducto=dt.codProducto
			 INNER JOIN unidadmedida um ON um.codUnidadMedida=dt.unidadingresada
       INNER JOIN notasalida nt ON dt.codNotaSalida=nt.codNotaSalida
