CREATE DEFINER=`root`@`%` PROCEDURE `MuestraFacturasProveedor`(alma int(11), codpro int(11))
BEGIN
SELECT 	fac.codFactura, fac.DocumentoFactura as documento ,IFNULL(pr.ruc,"") as ruc,
 IFNULL(pr.razonsocial,"") as razonsocial,IFNULL(pr.codProveedor,"")as codProveedor ,
fac.fechaingreso as fecha,
CONCAT(u.nombre," ",u.apellido) as responsable, fac.comentario,
(SELECT CASE fac.anulado when 0 then "Activo" when 1 Then "No Activo" END) as Estado
FROM 		facturacion fac
INNER JOIN usuario u on u.codUsuario = fac.codUsuario
LEFT OUTER JOIN proveedor pr on fac.codProveedor = pr.codProveedor 
LEFT OUTER JOIN notasalida ns ON fac.codFactura=ns.documentoreferencia 
WHERE fac.codalmacen=alma AND fac.codProveedor=codpro
and (ns.codTransaccion<>19 or ns.codTransaccion<>18)
ORDER BY fac.codFactura;
END
;;
DELIMITER ;

