-- ----------------------------
-- Procedure structure for MuestraGuiaRemision
-- ----------------------------
DROP PROCEDURE IF EXISTS `MuestraGuiaRemision`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `MuestraGuiaRemision`(codguia  int(11))
BEGIN
SELECT 	LPAD(gur.codGuiaRemision,11,'0'), gur.codAlmacen, gur.codMotivo, IFNULL(gur.codDocumento,0), IFNULL(td.sigla,""), 
        IFNULL(gur.codserie,0),IFNULL(se.serie, ""), IFNULL(gur.numdocumento,""), IFNULL(gur.codCliente,0), IFNULL(cl.codigopersonalizado,""), IFNULL(cl.dni,""), IFNULL(cl.ruc,""), IFNULL(cl.razonsocial,""),
  IFNULL(cl.nombre,""),IFNULL(cl.direccionentrega,"")
, IFNULL(gur.codVehiculoTransporte,0), IFNULL(vt.codmarca,0), IFNULL(vt.codmodelo,0), IFNULL(vt.placa,""), IFNULL(mt.descripcion,"") as marca
, IFNULL(mdt.descripcion,"") as modelo, IFNULL(vt.constanciainscripcion,""),IFNULL(gur.codConductor,0), IFNULL(cdt.nombre,""), IFNULL(cdt.licencia,"")
, IFNULL(gur.codEmpresaTransporte,0),IFNULL(et.ruc,""), IFNULL(et.razonsocial,""), IFNULL(et.direccion,""), gur.fechaemision, gur.fechatraslado, IFNULL(gur.comentario,"")
, gur.estado, gur.facturado, gur.codUser, gur.fecharegistro, IFNULL(gur.codPedido,0), IFNULL(gur.codFactura,0)
, IF(IFNULL(gur.codCliente,0)=0,(SELECT codalmacenreceptor FROM notasalida n WHERE n.codNotaSalida=gur.codFactura),0)as cod_cli
, IF(IFNULL(gur.codCliente,0)=0,(SELECT al.nombre FROM almacen al WHERE al.codAlmacen = (SELECT n.codalmacenreceptor FROM notasalida n 
        WHERE n.codNotaSalida=gur.codFactura)),'')as almacen_destino, 
  IF(IFNULL(gur.codCliente,0)=0,(SELECT al.ubicacion FROM almacen al WHERE al.codAlmacen = (SELECT n.codalmacenreceptor FROM notasalida n 
        WHERE n.codNotaSalida=gur.codFactura)),'')as ubalmacen_destino
FROM guiaremision gur LEFT OUTER JOIN tipodocumento td ON gur.codDocumento = td.codTipoDocumento
     LEFT OUTER JOIN serie se ON gur.codserie = se.codSerie
     LEFT OUTER JOIN cliente cl ON gur.codCliente = cl.codCliente
     LEFT JOIN vehiculotransporte vt ON vt.codVehiculoTransporte = gur.codVehiculoTransporte
     LEFT JOIN marcatransporte mt ON mt.codMarcaTransporte = vt.codmarca
     LEFT OUTER JOIN modelotransporte mdt ON mdt.codModeloTransporte = vt.codmodelo
     LEFT OUTER JOIN conductor cdt ON cdt.codConductor = gur.codConductor
     LEFT OUTER JOIN empresatransporte et ON et.codEmpresaTransporte = gur.codEmpresaTransporte
WHERE gur.codGuiaRemision = codguia
GROUP BY gur.codGuiaRemision;
END
;;
DELIMITER ;

