DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `MuestraKardexProducto`(criterio int(1), codalma int(11), fechaini datetime, fechafin datetime)
BEGIN
#Routine body goes here...
SELECT 		"NI" as tipo,LPAD(n.codNotaIngreso,11,'0') as codNota, tr.sigla as movimiento, n.fechaingreso as fecha, pr.ruc as proveedor,"" as cliente, td.sigla as documento, n.numdocumento, n.moneda, n.bruto
, n.montodscto,n.igv, n.total,n.formapago, n.fechapago
FROM 			notaingreso n INNER JOIN transaccion tr on tr.codTransaccion = n.codTransaccion
INNER JOIN proveedor pr on pr.codProveedor = n.codProveedor
INNER JOIN tipodocumento td on td.codTipoDocumento = n.codTipoDocumento
WHERE 		n.codAlmacen = codalma AND n.estado = 1 AND date(n.fechaingreso) BETWEEN fechaini AND fechafin
UNION
SELECT 		"NS" as tipo,LPAD(n.codNotaSalida,11,'0') as codNota, tr.sigla as movimiento, n.fechasalida as fecha,"" as proveedor,
(SELECT CASE cl.tipo WHEN 0 THEN cl.dni WHEN 1 THEN cl.ruc WHEN 2 THEN cl.ruc END) as cliente
, td.sigla as documento, n.numdocumento, n.moneda, n.bruto
, n.montodscto,n.igv, n.total,n.formapago, n.fechapago
FROM 			notasalida n 	INNER JOIN transaccion tr on tr.codTransaccion = n.codTransaccion
INNER JOIN cliente cl on cl.codCliente = n.codCliente
INNER JOIN tipodocumento td on td.codTipoDocumento = n.codTipoDocumento
WHERE 		n.codAlmacen = codalma AND n.estado = 1 AND date(n.fechasalida) BETWEEN fechaini AND fechafin
ORDER BY 	fecha ;
END
;;
