-- ----------------------------
-- Procedure structure for MuestraLineasdeCredito
-- ----------------------------
DROP PROCEDURE IF EXISTS `MuestraLineasdeCredito`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `MuestraLineasdeCredito`(codCli  int(11))
BEGIN
#Routine body goes here...
SELECT 	F.codFacturaV, CONCAT(td.sigla," ",LPAD(f.serie,4,0),"-",LPAD(f.numDocumento,6,0)) as documento,
				td.descripcion as tipodocumento, tr.sigla, f.fechasalida, f.fechapago, f.bruto as credito, 
				IFNULL(SUM(P.montocobrado),0) AS montocobrado, f.pendiente,
				if(IFNULL(f.pendiente,0) > 0,"Ingresar Pago","") as IngresarPago, 
				if(IFNULL(f.pendiente,0) < IFNULL(f.bruto,0),"MostrarPago","") as MostrarPagos


FROM factura_venta AS F
-- INNER JOIN detallefactura_venta AS DF ON F.codFacturaV = DF.codFacturaV
INNER JOIN tipodocumento as td ON td.codTipoDocumento = f.codTipoDocumento
INNER JOIN transaccion as tr ON tr.codTransaccion = f.codTransaccion
LEFT JOIN pago AS P ON F.codFacturaV = P.codNota AND p.ingresoegreso = 1 AND p.estado = 1

WHERE F.anulado = 0 AND F.estado = 1 AND F.cancelado = 0 AND F.codCliente = codCli AND f.pendiente > 0

GROUP BY F.codFacturaV;
END
;;
DELIMITER ;

