       IFNULL(nt.codalmacenemisor,0), 
       IFNULL((select ns.codconductor from notasalida ns WHERE ns.codNotaSalida = codNotSal),0) as codconductor,
       IFNULL((select ns.codVehiculoTransporte from notasalida ns WHERE ns.codNotaSalida = codNotSal),0) as codvehiculotransporte,nt.aplicada,
			 IFNULL(nt.codAplicada, 0),nt.horaIngreso, IFNULL(nt.codLoteProduccion,0),IFNULL(nt.codCentroCostos,0),IFNULL(nt.codIngresoCamara,0)
		   
