BEGIN

		SELECT DISTINCT(dfv.codFacturaV) as codVenta, fv.codTipoDocumento as codDocumento, 
           CASE fv.codTipoDocumento WHEN 1 THEN "BOLETA DE VENTA" WHEN 2 THEN "FACTURA" END AS NDocumento, 
           CONCAT(td.sigla,'-',fv.codFacturaV) as documento,
					 fv.fechasalida, fv.fecharegistro, fv.codUsuario as codUser, fp.codFormaPago as codFormaPago, fp.descripcion as Formapago, 
					 m.codMoneda as codMoneda, CASE fv.moneda WHEN 1 THEN "SOLES" WHEN 2 THEN "DOLARES" END AS Moneda, fv.codListaPrecio AS codLista,lp.nombre AS Nlista, fv.tipocambio, 
					(SELECT SUM(pa.stockdisponible) FROM productoalmacen pa LEFT OUTER JOIN producto pro ON pa.codProducto=pro.codProducto
						WHERE pa.codAlmacen=codalma) AS stockDisponible, cli.codCliente, cli.codigopersonalizado, cli.razonsocial, 
          IF(cli.dni = '', cli.ruc, cli.dni)AS ruc_dni, cli.direccionlegal

		FROM factura_venta fv INNER JOIN cliente cli ON fv.codCliente=cli.codCliente 
				 INNER JOIN tipodocumento td ON fv.codTipoDocumento=td.codTipoDocumento 
				 INNER JOIN formapago fp ON fv.formapago = fp.codFormaPago
				 INNER JOIN moneda m ON m.codMoneda = fv.moneda
				 INNER JOIN detallefactura_venta dfv ON fv.codFacturaV = dfv.codFacturaV
				 INNER JOIN listaprecio lp ON lp.codListaPrecio = fv.codListaPrecio
         INNER JOIN almacen al ON al.codAlmacen=fv.codAlmacen
         

		WHERE fv.codAlmacen=codalma AND fv.estado=1 AND fv.codTransaccion=7 AND dfv.cantidadpendiente > 0 AND dfv.estado=1 
          AND fv.codSucursal=sucur AND al.codEmpresa=empre
