DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `MuestraVentas`(codalma int(11), fechaini datetime, fechafin datetime)
BEGIN
#Routine body goes here...
SELECT 	LPAD(n.codNotaSalida,6,0) as codNota, n.fechasalida as fecha,td.sigla as documento, CONCAT(n.serie, ' - ', n.numdocumento) as numdoc
	,cl.codigopersonalizado as codcliente, cl.razonsocial as cliente, (SELECT CASE n.moneda WHEN 0 THEN "SOLES" WHEN 1 THEN "DOLARES" END) AS moneda
	,n.total,fp.descripcion as formapago, n.fechapago, (SELECT CASE n.anulado WHEN 0 THEN "ACTIVO" WHEN 1 THEN "ANULADO" END) AS anulado
FROM 			notasalida n 
LEFT OUTER JOIN cliente cl on cl.codCliente = n.codCliente
LEFT OUTER JOIN tipodocumento td on td.codTipoDocumento = n.codTipoDocumento
LEFT OUTER JOIN formapago fp ON fp.codFormaPago = n.formapago
