DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `MuestraVentas_FV`(codalma int(11), fechaini datetime, fechafin datetime)
BEGIN
	SELECT LPAD(n.codFacturaV,6,0) as codFactura, n.fechasalida as fecha,td.sigla as documento, 
         CONCAT(n.serie,'-', n.numdocumento) as numdoc
	,IF(cl.dni='',cl.ruc,cl.dni)as codcliente, cl.razonsocial as cliente, (SELECT CASE n.moneda WHEN 1 THEN "SOLES" WHEN 2 THEN "DOLARES" END) AS moneda
	,n.total,fp.descripcion as formapago, n.fechapago, (SELECT CASE n.anulado WHEN 0 THEN "ACTIVO" WHEN 1 THEN "ANULADO" END) AS anulado, 
  (SELECT CASE n.impreso WHEN 0 THEN "NO IMPRESO" WHEN 1 THEN "IMPRESO" END) AS impreso, activarVista AS vista
FROM 	factura_venta n 
INNER JOIN cliente cl on cl.codCliente = n.codCliente
INNER JOIN tipodocumento td on td.codTipoDocumento = n.codTipoDocumento
INNER JOIN formapago fp ON fp.codFormaPago = n.formapago
WHERE n.estado = 1 AND date(n.fechasalida) BETWEEN DATE(fechaini) AND date(fechafin) AND n.codTransaccion = 7 
	 	AND ((activarVista=0 and n.codAlmacen = codalma) or activarVista=1)
ORDER BY 	n.fechasalida DESC, n.codFacturaV DESC;

END
;;
