DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ReporteDespachoDocumento`(codalma int(11), fecha1 datetime, fecha2 datetime)
BEGIN

SELECT ns.codTipoDocumento, ns.codSerie, t.sigla, CONCAT(ns.serie,'-',ns.numdocumento)AS documento, ns.codCliente, c.codigopersonalizado,
      CASE ns.codTipoDocumento WHEN 1 THEN c.nombre WHEN 2 THEN c.razonsocial END AS nombre_razon,
      CASE ns.codTipoDocumento WHEN 1 THEN c.dni WHEN 2 THEN c.ruc END AS dni_ruc,ns.total,
      DATE_FORMAT(ns.fechasalida,'%d-%m-%Y') AS fecha_salida, DATE_FORMAT(fecha1,'%d-%m-%Y')AS fecha_consulta, 
      e.razonsocial,ns.formapago,fp.descripcion,fp.dias,
(SELECT SUM(n.total) FROM factura_venta n LEFT JOIN formapago f ON f.codFormaPago = n.formapago WHERE f.tipo = 1 AND n.anulado = 0 AND n.estado = 1 AND DATE(n.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)) AS contado,
(SELECT SUM(n.total) FROM factura_venta n LEFT JOIN formapago f ON f.codFormaPago = n.formapago WHERE f.tipo = 0 AND n.anulado = 0 AND n.estado = 1 AND DATE(n.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)) AS credito,
DATE_FORMAT(fecha2,'%d-%m-%Y')AS fecha2, DATE_FORMAT(fecha1,'%d-%m-%Y')AS fecha1
FROM factura_venta ns LEFT JOIN cliente c ON c.codCliente = ns.codCliente 
     LEFT JOIN detallefactura_venta dns ON dns.codFacturaV = ns.codFacturaV 
     LEFT JOIN almacen al ON dns.codAlmacen = al.codAlmacen 
     LEFT JOIN empresa e ON e.codEmpresa = al.codEmpresa 
     LEFT JOIN tipodocumento t ON t.codTipoDocumento = ns.codTipoDocumento 
     LEFT JOIN formapago fp ON fp.codFormaPago = ns.formapago
WHERE al.codAlmacen = codalma AND ns.anulado = 0 AND ns.estado = 1 AND DATE(ns.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)
GROUP BY ns.codFacturaV
ORDER BY ns.codTipoDocumento;

END
;;
DELIMITER ;

