ON pv.codProveedor = ni.codProveedor LEFT JOIN transaccion tr
ON tr.codTransaccion = k.codTransaccion LEFT JOIN familia f
ON f.codFamilia = p.codFamilia LEFT JOIN linea l
ON l.codFamilia = f.codFamilia LEFT JOIN grupo g
ON g.codLinea = l.codLinea LEFT JOIN productoalmacen pa
ON pa.codProducto = p.codProducto LEFT JOIN almacen al
ON al.codAlmacen = pa.codAlmacen LEFT JOIN empresa e
