WHERE p.referencia = ref AND k.estado = 0 AND k.fechatransaccion BETWEEN fecha1 AND fecha2
GROUP BY k.item
ORDER BY k.item ASC;
END IF;
IF todo = 1 THEN
SELECT k.item,f.codFamilia,f.descripcion AS fam,l.codLinea,l.descripcion AS lin,g.codGrupo,g.descripcion AS gru,p.codProducto,p.referencia,p.descripcion,k.fechatransaccion,
IFNULL(ns.codTipoDocumento,ni.codTipoDocumento) AS tip,t.sigla,IFNULL(ns.serie,ni.serie) AS ser,
IFNULL(ns.numdocumento,ni.numdocumento) AS num,
CASE tr.tipo WHEN 0 THEN tr.descripcion WHEN 1
THEN tr.descripcion END AS operacion,
