IF(tr.tipo=1,k.total,0) AS s_total,k.stock,k.valorpromedio,k.saldo,fecha1,fecha2,e.razonsocial,al.descripcion AS alma,
IF(IFNULL(k.coddetalleingreso,"") = "",(k.stock + k.cantidad),(k.stock - k.cantidad)) AS stock_inicial, IF(IFNULL(k.coddetalleingreso,"") = "",(k.saldo + k.total),(k.saldo + k.total)) AS saldo_inicial
FROM kardex k LEFT JOIN detallenotaingreso dni
ON dni.codDetalleIngreso = k.coddetalleingreso LEFT JOIN detallenotasalida dns
ON dns.codDetalleSalida = k.coddetallesalida LEFT JOIN notaingreso ni
