ON ns.codNotaSalida = dns.codNotaSalida LEFT JOIN producto p
ON p.codProducto = k.codproducto LEFT JOIN cliente c
ON c.codCliente = ns.codCliente LEFT JOIN proveedor pv
ON pv.codProveedor = ni.codProveedor LEFT JOIN transaccion tr
ON tr.codTransaccion = k.codTransaccion LEFT JOIN familia f
ON f.codFamilia = p.codFamilia LEFT JOIN linea l
