CREATE DEFINER=`root`@`localhost` PROCEDURE `ReporteStatusCajaChica`(fechaini date, fechafin date)
BEGIN

SELECT 	IFNULL((SELECT montoApertura FROM aperturacierre WHERE  estado = 1
AND DATE(fecharegistro) = fechaini ),0) as AperturaCaja,

IFNULL((SELECT SUM(monto) FROM flujocaja WHERE tipo = 1 AND estado = 1
AND DATE(fecha) BETWEEN fechaini and fechafin),0) as ingreso,

IFNULL((SELECT SUM(monto) FROM flujocaja WHERE tipo = 2 AND estado = 1
AND DATE(fecha) BETWEEN fechaini and fechafin),0) as egreso,

IFNULL((SELECT SUM(abonado) from factura_venta WHERE anulado = 0 and estado = 1
AND DATE(fechapago) BETWEEN fechaini and fechafin),0)as TotalVentas,

IFNULL((SELECT SUM(pendiente) from factura_venta WHERE anulado = 0 and estado = 1 and codTransaccion = 7
AND DATE(fechapago) BETWEEN fechaini and fechafin),0)as PorCobrar,

IFNULL((SELECT SUM(abonado) from notaingreso WHERE anulado = 0 and estado = 1 
            AND DATE(fechapago) BETWEEN fechaini and fechafin
        UNION
        SELECT SUM(abonado) from facturacion WHERE anulado = 0 and estado = 1
            AND DATE(fechapago) BETWEEN fechaini and fechafin),0)as TotalPagos,

IFNULL((SELECT SUM(pendiente) from notaingreso WHERE anulado = 0 and estado = 1 and codTransaccion = 1
            AND DATE(fechapago) BETWEEN fechaini and fechafin
        UNION
        SELECT SUM(pendiente) from facturacion WHERE anulado = 0 and estado = 1 and codTransaccion IN(1,14)
               AND DATE(fechapago) BETWEEN fechaini and fechafin),0)as PorPagar,

IFNULL((SELECT SUM(montoApertura) FROM aperturacierre WHERE  estado = 1
AND DATE(fecharegistro) BETWEEN ADDDATE(fechaini,1) AND fechafin),0) as SumaAperturasCaja,

IFNULL((SELECT SUM(montoCierre) FROM aperturacierre WHERE  estado = 1
AND DATE(fecharegistro) BETWEEN fechaini AND ADDDATE(fechafin,-1)),0) as SumaCierresCaja;
END
;;
DELIMITER ;

