DROP PROCEDURE IF EXISTS `ReporteVentas`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ReporteVentas`(fecha1 date, fecha2 date)
BEGIN
	#Routine body goes here...
SELECT ns.codNotaSalida, DATE(pa.fecharegistro) as fecha, 
			 pa.montopagado as abonado, SUM(dns.subtotal) as monto, 			 
			 SUBSTR(IFNULL(cl.nombre,cl.ruc),1,30)  as cliente, 
			 CONCAT(v.nombre," ",v.apellido) as vendedor,st.numero as stand, 
			 m.descripcion as metodo,IFNULL(pa.noperacion,"--- ---") as noperacion, 
			 IFNULL(tp.tipo,"--- ---") as tarjeta


FROM notasalida as ns 
INNER JOIN detallenotasalida as dns ON ns.codNotaSalida = dns.codNotaSalida
INNER JOIN pago as pa ON ns.codNotaSalida = pa.codNota
INNER JOIN vendedor as v ON v.codVendedor = ns.codVendedor 
INNER JOIN cliente as cl ON cl.codCliente = ns.codCliente
INNER JOIN stand as st ON st.codstand = ns.codStand
LEFT JOIN metodopago as m ON m.codMetodoPago = pa.codTipoPago
LEFT JOIN tarjetaspago as tp ON tp.codtarjeta = pa.codTarjetasPago

WHERE  DATE(pa.fecharegistro) BETWEEN DATE(fecha1) AND DATE(fecha2) AND ns.anulado = 0
GROUP BY pa.codNota;

END
;;
DELIMITER ;

