           LEFT JOIN sucursal suc ON ns.codSucursal = suc.codSucursal
			
      WHERE ns.estado = 1 AND ns.anulado = 0 AND suc.codSucursal= empre AND DATE(ns.fechasalida) BETWEEN fecha1 AND fecha2;
