       IF(gr.codEmpresaTransporte=0,IFNULL(vt.placa,""),"")AS placa, IF(gr.codEmpresaTransporte=0,IFNULL(mt.descripcion,""),"")AS modelo,
       IF(gr.codEmpresaTransporte=0,IFNULL(mat.descripcion,""),"")AS marca, 
       IF(gr.codMotivo!=5,CONCAT(td.sigla," ",s.serie,"-",fv.numDocumento),"")as documento,confVehicular

FROM guiaremision gr LEFT OUTER JOIN serie s ON gr.codserie=s.codSerie
				 LEFT OUTER JOIN almacen al ON gr.codAlmacen=al.codAlmacen
				 LEFT OUTER JOIN empresa em ON al.codEmpresa=em.codEmpresa
				 LEFT OUTER JOIN conductor con ON gr.codConductor=con.codConductor
				 LEFT OUTER JOIN vehiculotransporte vt ON gr.codVehiculoTransporte=vt.codVehiculoTransporte
				 LEFT OUTER JOIN detalleguiaremision dgr ON gr.codGuiaRemision=dgr.codGuiaRemision
				 LEFT OUTER JOIN unidadmedida um ON um.codUnidadMedida=dgr.unidadingresada
				 LEFT OUTER JOIN producto pr ON pr.codProducto=dgr.codProducto
         LEFT OUTER JOIN cliente cl ON cl.codCliente=gr.codCliente
         LEFT OUTER JOIN modelotransporte mt ON mt.codModeloTransporte=vt.codmodelo
         LEFT OUTER JOIN marcatransporte mat ON mat.codMarcaTransporte=vt.codmarca
         LEFT OUTER JOIN factura_venta fv ON fv.codFacturaV=gr.codFactura
         LEFT OUTER JOIN tipodocumento td ON fv.codTipoDocumento=td.codTipoDocumento
         LEFT OUTER JOIN empresatransporte emt ON emt.codEmpresaTransporte=gr.codEmpresaTransporte
         LEFT OUTER JOIN notasalida ns ON ns.codNotaSalida=gr.codFactura
         
WHERE gr.codGuiaRemision=codguia AND gr.codAlmacen=codalma and gr.estado=1 
GROUP BY dgr.codProducto;

ELSE

SELECT DISTINCT(ns.codNotaSalida), gr.numdocumento, s.serie, DATE_FORMAT(gr.fechaemision,"%d-%m-%Y")AS fecharemision, DATE_FORMAT(gr.fechatraslado,"%d-%m-%Y")AS fechatraslado, 
       em.direccion, em.razonsocial as empresa, em.ruc as ruc_empresa, gr.codConductor, con.dni, IFNULL(con.ruc,"")as ruc_conductor, 
       gr.codVehiculoTransporte, IFNULL(vt.constanciainscripcion,"")as constanciainscripcion, 
			 CASE gr.codMotivo WHEN 0 THEN "Venta" WHEN 1 THEN "Venta sujeta a confirmación del comprador"
					WHEN 2 THEN "Compra" WHEN 3 THEN "Consignación" WHEN 4 THEN "Devolución" WHEN 5 THEN "Entre establecimientos de la misma empresa" 
					WHEN 6 THEN "De bienes para transformación"  WHEN 7 THEN "Recojo de Bienes" WHEN 8 THEN "Por emisor Itinerante de comp. de pago" 
					WHEN 9 THEN "Zona primaria" WHEN 10 THEN "Importación" WHEN 11 THEN "Exportación" WHEN 12 THEN "Venta con entrega a terceros" 
					WHEN 13 THEN "Otros" END AS motivo, dgr.cantidad, dgr.unidadingresada, dgr.codProducto, pr.referencia, pr.descripcion as producto,
			 um.descripcion as unidad, al.nombre as almacenorigen, IFNULL(obtienealmacen(ns.codalmacenreceptor,1),"")as almacendestino,
       al.ubicacion as ub_almacenorigen, IFNULL(obtienealmacen(ns.codalmacenreceptor,2),"")as ub_almacendestino, IFNULL(gr.codCliente,0)as codCliente,
       IFNULL(cl.razonsocial,"")as cliente, IFNULL(cl.direccionlegal,"")AS dir_cliente, IFNULL(IFNULL(cl.ruc,cl.dni),"")as rucdni_cli,  
       dgr.peso, um.sigla as sig_unidad, IF(gr.codEmpresaTransporte=0,con.nombre,emt.razonsocial)as transportista,
       IF(gr.codEmpresaTransporte!=0,emt.ruc,"")as ruc_transportista, IF(gr.codEmpresaTransporte=0,con.licencia,"")as licencia,
       IF(gr.codEmpresaTransporte=0,IFNULL(vt.placa,""),"")AS placa, IF(gr.codEmpresaTransporte=0,IFNULL(mt.descripcion,""),"")AS modelo,
       IF(gr.codEmpresaTransporte=0,IFNULL(mat.descripcion,""),"")AS marca, 
       IF(gr.codMotivo!=5,CONCAT(td.sigla," ",s.serie,"-",fv.numDocumento),"")as documento, confVehicular

FROM guiaremision gr LEFT OUTER JOIN serie s ON gr.codserie=s.codSerie
				 LEFT OUTER JOIN almacen al ON gr.codAlmacen=al.codAlmacen
				 LEFT OUTER JOIN empresa em ON al.codEmpresa=em.codEmpresa
				 LEFT OUTER JOIN conductor con ON gr.codConductor=con.codConductor
				 LEFT OUTER JOIN vehiculotransporte vt ON gr.codVehiculoTransporte=vt.codVehiculoTransporte
				 LEFT OUTER JOIN detalleguiaremision dgr ON gr.codGuiaRemision=dgr.codGuiaRemision
				 LEFT OUTER JOIN unidadmedida um ON um.codUnidadMedida=dgr.unidadingresada
				 LEFT OUTER JOIN producto pr ON pr.codProducto=dgr.codProducto
         LEFT OUTER JOIN cliente cl ON cl.codCliente=gr.codCliente
         LEFT OUTER JOIN modelotransporte mt ON mt.codModeloTransporte=vt.codmodelo
         LEFT OUTER JOIN marcatransporte mat ON mat.codMarcaTransporte=vt.codmarca
         LEFT OUTER JOIN factura_venta fv ON fv.codFacturaV=gr.codFactura
         LEFT OUTER JOIN tipodocumento td ON fv.codTipoDocumento=td.codTipoDocumento
         LEFT OUTER JOIN empresatransporte emt ON emt.codEmpresaTransporte=gr.codEmpresaTransporte
         LEFT OUTER JOIN notasalida ns ON ns.codNotaSalida=gr.codFactura
         
WHERE ns.numdocumento=codguia AND gr.codAlmacen=codalma and gr.estado=1 and ns.codTransaccion = 15 and gr.codMotivo=5
GROUP BY dgr.codProducto;

END IF;
END
;;
DELIMITER ;

