DROP PROCEDURE IF EXISTS `ReportLiquidacionCajaDia`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `ReportLiquidacionCajaDia`(fec datetime)
BEGIN
SELECT IFNULL((SELECT SUM(monto) FROM flujocaja WHERE tipo = 1 AND estado = 1
AND DATE(fecha) = DATE(fec)),0) as ingreso,
IFNULL((SELECT SUM(monto) FROM flujocaja WHERE tipo = 2 AND estado = 1
AND DATE(fecha) = DATE(fec)),0) as egreso,
fv.abonado, fv.pendiente, fv.total,
IFNULL((SELECT SUM(fv.abonado) from factura_venta fv INNER JOIN pago p ON fv.codFacturaV=p.codNota 
        WHERE fv.anulado = 0 and fv.estado = 1 and fv.formapago<>6
AND DATE(p.fechapago) = DATE(fec)),0)as TotalVentasCredito, 
fc.concepto, IF(cl.dni="",cl.ruc,cl.dni) as dni_ruc,
cl.razonsocial, CASE p.codTipoPago WHEN 5 THEN "EFECTIVO" WHEN 6 THEN "DEPOSITO" WHEN 7 THEN "CHEQUE"
    WHEN 8 THEN "TARJETA" WHEN 9 THEN "TRANSFERENCIA" END AS tipo

FROM flujocaja fc RIGHT OUTER JOIN factura_venta fv ON fc.codAlmacen=fv.codAlmacen
      RIGHT OUTER JOIN cliente cl ON fv.codCliente=cl.codCliente
      RIGHT OUTER JOIN pago p ON fv.codFacturaV=p.codNota
      RIGHT OUTER JOIN tipodocumento td ON fv.codTipoDocumento=td.codTipoDocumento
GROUP BY fc.codFlujoCaja;
END
;;
