DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ReportNotaCreditoVenta`(codnota int(11), codalma int(11))
BEGIN

SELECT nt.codNotaIngreso as codNota, IFNULL(cl.codigopersonalizado,'')as codpercli, IFNULL(cl.ruc,cl.dni)as rucdnicli,
       IFNULL(cl.razonsocial,'')as cliente, IFNULL(cl.direccionlegal,'')as dircli, DATE_FORMAT(nt.fechaingreso,'%d-%m-%Y')as fecha,
       fv.codTipoDocumento, IF(fv.codTipoDocumento=1,'Boleta de Venta','Factura')as documento, 
       CONCAT(fv.serie,'-',fv.numDocumento)as ndocref, DATE_FORMAT(fv.fechasalida,'%d-%m-%Y')as fechasalida, 
       IFNULL(dnt.cantidad,0)as cantidad, IFNULL(dnt.preciounitario,0)as preciounitario, IFNULL(nt.comentario,'')as motivo, 
       IFNULL(dnt.subtotal,0)as valorventadet, IFNULL(dnt.igv,0)as igvdet,  IFNULL(dnt.importe,0)as totaldet,
       dnt.codProducto, IFNULL(p.referencia,'')as referencia, IFNULL(p.descripcion,'')as producto, dnt.unidadingresada,
       IFNULL(u.descripcion,'')as unidad, IFNULL(u.sigla,'')as siguni, nt.igv, nt.bruto as valorventa, nt.total
       
FROM detallenotaingreso dnt INNER JOIN notaingreso nt ON dnt.codNotaIngreso=nt.codNotaIngreso
     INNER JOIN factura_venta fv ON nt.codReferencia=fv.codFacturaV INNER JOIN cliente cl ON cl.codCliente=fv.codCliente
     INNER JOIN tipodocumento td ON fv.codTipoDocumento=td.codTipoDocumento INNER JOIN producto p ON dnt.codProducto=p.codProducto
		 INNER JOIN unidadmedida u ON dnt.unidadingresada=u.codUnidadMedida
WHERE nt.codNotaIngreso = codnota AND nt.estado=1 AND dnt.estado=1 AND fv.estado=1 AND nt.codTransaccion=20 AND nt.codAlmacen=codalma;

END
;;
DELIMITER ;

