DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ReportNotaDebitoVenta`(codventa int(11), codalma int(11))
BEGIN
	SELECT fv.codFacturaV as codventa, IFNULL(cl.codigopersonalizado,'')as codpercli, IFNULL(cl.ruc,cl.dni)as dnicli,
       IFNULL(cl.razonsocial,'')as cliente, IFNULL(cl.direccionlegal,'')as dircli, IFNULL(fv.comentario,'')as motivo, 
       IFNULL((SELECT CONCAT(t.sigla,' ',f.serie,'-',LPAD(f.numDocumento,4,0))
               FROM factura_venta f INNER JOIN tipodocumento t ON f.codTipoDocumento=t.codTipoDocumento
               WHERE f.codFacturaV = fv.documentoreferencia),'')as docref, fv.formapago, fp.descripcion as formap, 
       DATE_FORMAT(fv.fechapago,'%d-%m-%Y')as fechapago, DATE_FORMAT(fv.fechasalida,'%d-%m-%Y')as fechasalida, fv.moneda, 
       m.descripcion as desmon, IF(IFNULL(fv.comentario,'')='Intereses','',IFNULL(dfv.cantidad,0))as cantidad, 
       IF(IFNULL(fv.comentario,'')='Intereses','',IFNULL(dfv.preciounitario,0))as preciouni, IFNULL(dfv.subtotal,0)as valorventadet, 
       IFNULL(dfv.igv,0)as igvdet, IFNULL(dfv.importe,0)as totaldet, dfv.codProducto, IFNULL(fv.bruto,0)as valorventa, 
       IFNULL(fv.igv,0)as igv, IFNULL(fv.total,0)as total,
       IF(IFNULL(fv.comentario,'')='Intereses','', IFNULL(p.referencia,''))as referencia, p.descripcion, dfv.unidadingresada,
       u.descripcion as unidad, DATE_FORMAT(fv.fecharegistro,'%d-%m-%Y')as fecharegistro, fv.codUsuario, 
       CONCAT(usu.nombre,' ',usu.apellido)as usuario, fv.codAlmacen, al.nombre as almacen
FROM factura_venta fv INNER JOIN detallefactura_venta dfv ON fv.codFacturaV=dfv.codFacturaV
     INNER JOIN cliente cl ON fv.codCliente=cl.codCliente INNER JOIN formapago fp ON fv.formapago=fp.codFormaPago
     INNER JOIN moneda m ON fv.moneda= m.codMoneda INNER JOIN tipodocumento td ON fv.codTipoDocumento=td.codTipoDocumento
     INNER JOIN producto p ON dfv.codProducto=p.codProducto INNER JOIN unidadmedida u ON dfv.unidadingresada=u.codUnidadMedida
     INNER JOIN usuario usu ON usu.codUsuario=fv.codUsuario INNER JOIN almacen al ON fv.codAlmacen=al.codAlmacen
WHERE fv.codFacturaV = codventa AND fv.estado = 1 AND dfv.estado=1 AND fv.codAlmacen=codalma AND fv.codTransaccion=21;

END
;;
DELIMITER ;

