DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `ReportVentCredContDia`(empre int(11), fecha1 date, fecha2 date, forma int(11),su int(11), alma int(11))
BEGIN
	IF forma = 0 THEN

			SELECT ns.codFacturaV AS codFacturaV,
			IF(forma = 0,"TODOS",fp.descripcion) AS cabecera,s.serie, CONCAT(td.sigla,'-',ns.serie,'-',ns.numDocumento) as numdocumento,
      ns.codTipoDocumento,td.sigla,c.codCliente,c.codigopersonalizado, IFNULL(c.ruc,c.dni) AS ruc_dni, 
      IFNULL(c.razonsocial,c.nombre) AS razon_nombre,DATE_FORMAT(ns.fechasalida,'%d-%m-%Y')AS fechasalida, 
      DATE_FORMAT(ns.fechapago,'%d-%m-%Y')AS fechapago,CASE ns.moneda WHEN 1 THEN "SOLES" WHEN 2 THEN "DOLARES" END AS moneda ,ns.total,
			ns.pendiente, c.banco, c.ctacte,CASE ns.cancelado WHEN 0 THEN "PENDIENTE" WHEN 1 THEN "CANCELADO" END AS vigencia, 
      DATE_FORMAT(fecha1,'%d-%m-%Y')AS fecha1, DATE_FORMAT(fecha2,'%d-%m-%Y')AS fecha2,

			(SELECT SUM(n.total) FROM factura_venta n LEFT JOIN formapago f ON f.codFormaPago = n.formapago
			WHERE n.anulado = 0 AND n.estado = 1 AND f.tipo = 1 AND DATE(n.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)) AS contado
			,(SELECT SUM(n.total) FROM factura_venta n LEFT JOIN formapago f ON f.codFormaPago = n.formapago
			WHERE n.anulado = 0 AND n.estado = 1 AND f.tipo = 0 AND DATE(n.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)) AS credito,
      CASE ns.codTipoDocumento WHEN 1 THEN "BOLETA DE VENTA" WHEN 2 THEN "FACTURA" END AS tipoDocumento,
      IF(ns.formapago=6,"VENTAS AL CONTADO","VENTAS A CREDITO")AS tipoVenta
      
      FROM factura_venta ns LEFT JOIN cliente c ON c.codCliente = ns.codCliente 
           LEFT JOIN formapago fp ON fp.codFormaPago = ns.formapago 
           LEFT JOIN almacen al ON ns.codAlmacen = al.codAlmacen 
           LEFT JOIN tipodocumento td ON td.codTipoDocumento=ns.codTipoDocumento 
           LEFT JOIN serie s ON ns.codSerie = s.codSerie
           LEFT JOIN sucursal suc ON ns.codSucursal = suc.codSucursal
           LEFT JOIN empresa em ON al.codEmpresa=em.codEmpresa 
			
      WHERE ns.estado = 1 AND ns.anulado = 0 AND suc.codSucursal= su AND em.codEmpresa= empre AND al.codAlmacen=alma AND 
            DATE(ns.fechasalida) BETWEEN fecha1 AND fecha2;

  ELSE

			SELECT ns.codFacturaV AS codFacturaV,
			IF(forma = 0,"TODOS",fp.descripcion) AS cabecera,s.serie, CONCAT(td.sigla,'-',ns.serie,'-',ns.numDocumento) as numdocumento,
      ns.codTipoDocumento,td.sigla,c.codCliente,c.codigopersonalizado, IFNULL(c.ruc,c.dni) AS ruc_dni, 
      IFNULL(c.razonsocial,c.nombre) AS razon_nombre,DATE_FORMAT(ns.fechasalida,'%d-%m-%Y')AS fechasalida, 
      DATE_FORMAT(ns.fechapago,'%d-%m-%Y')AS fechapago,CASE ns.moneda WHEN 1 THEN "SOLES" WHEN 2 THEN "DOLARES" END AS moneda ,ns.total,
			ns.pendiente, c.banco, c.ctacte,CASE ns.cancelado WHEN 0 THEN "PENDIENTE" WHEN 1 THEN "CANCELADO" END AS vigencia, 
      DATE_FORMAT(fecha1,'%d-%m-%Y')AS fecha1, DATE_FORMAT(fecha2,'%d-%m-%Y')AS fecha2,

			(SELECT SUM(n.total) FROM factura_venta n LEFT JOIN formapago f ON f.codFormaPago = n.formapago
			WHERE n.anulado = 0 AND n.estado = 1 AND f.tipo = 1 AND DATE(n.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)) AS contado
			,(SELECT SUM(n.total) FROM factura_venta n LEFT JOIN formapago f ON f.codFormaPago = n.formapago
			WHERE n.anulado = 0 AND n.estado = 1 AND f.tipo = 0 AND DATE(n.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)) AS credito,
      CASE ns.codTipoDocumento WHEN 1 THEN "BOLETA DE VENTA" WHEN 2 THEN "FACTURA" END AS tipoDocumento,
