CREATE DEFINER=`root`@`%` PROCEDURE `ReportVentxVendedor`(empre int(11), fecha1 datetime, fecha2 datetime, forma int(11), cod int(11))
BEGIN
IF cod = 0 AND forma = 0 THEN
	
  SELECT fv.codCliente,c.codigopersonalizado, IFNULL(c.ruc,c.dni)AS ruc_dni, c.nombre, c.razonsocial, td.sigla, CONCAT(td.sigla,"-",s.serie,"-",fv.numDocumento) AS num_doc, fv.total,
	IF(forma = 0,"TODOS",fp.descripcion) AS cabecera, IFNULL(v.codVendedor,0)AS codVendedor, IFNULL(v.dni,"")as dniVen, IFNULL(v.apellido,"")AS nomVen, DATE_FORMAT(fv.fechasalida,'%d-%m-%Y') AS fechasalida, DATE_FORMAT(fv.fechapago,'%d-%m-%Y') AS fechapago,
	IF(c.zona = 0,"TODOS",z.descripcion) AS zona,z.descripcion AS zona1,DATE_FORMAT(fecha1,'%d-%m-%Y') AS fecha1, DATE_FORMAT(fecha2,'%d-%m-%Y') AS fecha2, e.razonsocial,
	(SELECT SUM(n.total) FROM factura_venta n LEFT JOIN formapago f ON f.codFormaPago = n.formapago WHERE f.tipo = 1 AND n.anulado = 0 AND n.estado = 1 AND DATE(n.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)) AS contado,
	(SELECT SUM(n.total) FROM factura_venta n LEFT JOIN formapago f ON f.codFormaPago = n.formapago WHERE f.tipo = 0 AND n.anulado = 0 AND n.estado = 1 AND DATE(n.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)) AS credito
	
  FROM factura_venta fv LEFT OUTER JOIN detallefactura_venta ds ON ds.codFacturaV=fv.codFacturaV
     LEFT OUTER JOIN vendedor v ON fv.codVendedor=v.codVendedor 
     LEFT OUTER JOIN formapago fp ON fp.codFormaPago = fv.formapago	
     LEFT OUTER JOIN almacen al ON fv.codAlmacen = al.codAlmacen 
     LEFT OUTER JOIN empresa e ON e.codEmpresa = al.codEmpresa 
     LEFT OUTER JOIN cliente c ON c.codCliente = fv.codCliente 
     LEFT OUTER JOIN tipodocumento td ON td.codTipoDocumento = fv.codTipoDocumento 
     LEFT OUTER JOIN zona z ON c.zona = z.codZona
     LEFT OUTER JOIN sucursal su ON fv.codSucursal=su.codSucursal
     LEFT OUTER JOIN serie s ON fv.codSerie=s.codSerie

	WHERE al.codAlmacen = empre AND fv.anulado = 0 AND fv.estado = 1 AND DATE(fv.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)
	GROUP BY fv.codFacturaV
