CREATE DEFINER=`root`@`%` PROCEDURE `rollbackfactura`(codventa int(11))
BEGIN
	
  UPDATE serie SET numeracion = numeracion - 1 WHERE codserie = (SELECT codSerie FROM factura_venta WHERE codFacturaV = codventa);

	DELETE FROM factura_venta WHERE codFacturaV = codventa;

  DELETE FROM notasalida WHERE codNotaSalida = codventa;
	
END
