		(CASE codmoneda WHEN 1 THEN 'SOLES' WHEN 2 THEN 'DOLARES' END ) AS moneda,
		(CASE valorizado WHEN TRUE THEN 'Valorizado' WHEN FALSE THEN '' END ) AS valorizado,
		fecha1,fecha2,desdecosto,hastacosto,desdeart,hastaart,p.referencia,p.descripcion,
		ns.fechasalida as fechaingreso,tr.sigla, CONCAT(td.sigla,ns.numdocumento) as numdocumento ,dns.cantidad,
		 (CASE codmoneda WHEN 1 THEN dns.preciounitario WHEN 2 THEN ROUND(dns.preciounitario/tc,2)  END ) AS preciounitario,
		(CASE valorizado WHEN TRUE THEN (CASE codmoneda WHEN 1 THEN ROUND( dns.preciounitario*dns.cantidad,2) WHEN 2 THEN ROUND( (dns.preciounitario/tc) *dns.cantidad ,2) END )
     WHEN FALSE THEN '' END ) AS importe
	#	(CASE valorizado WHEN TRUE THEN dns.importe WHEN FALSE THEN '' END ) AS importe
		FROM detallenotasalida dns 
		INNER JOIN producto p on p.codProducto = dns.codProducto
		INNER JOIN notasalida ns on ns.codNotaSalida = DNS.codNotaSalida
		INNER JOIN con_centrocosto cc on ns.codCentroCostos = cc.codigocentrocosto
		INNER JOIN transaccion tr on tr.codTransaccion = ns.codTransaccion
		INNER JOIN tipodocumento td on td.codTipoDocumento = ns.codTipoDocumento
		WHERE 
		cc.codigocentrocosto BETWEEN desdecosto AND hastacosto 
#		AND p.referencia BETWEEN desdeart and hastaart 
		AND ns.fechasalida BETWEEN fecha1 AND fecha2
		AND cc.nivel = codnivel
		AND ns.moneda = codmoneda
		AND ns.codAlmacen = codalm
		GROUP BY dns.codProducto;


	END IF;


END IF;








END
;;
DELIMITER ;

