DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `VerificaProductoBonificadoCompra`(codalmacen_ex int(11), usu int(11))
BEGIN
	SELECT dt.codNotaIngreso as codNota, dt.codDetalleIngreso as codDetalle, dt.codProducto AS codProducto, p.referencia,
		p.descripcion as producto, dt.moneda, u.codUnidadMedida, u.descripcion as unidad,dt.serielote, 
		sum(dt.cantidadpendiente) as cantidad,
		ifnull( 
		(SELECT DISTINCT(obtieneprecio(codProducto,dt.codNotaIngreso,1)) FROM detalleorden_compra WHERE codproducto = dt.codProducto
     AND codAlmacen=codalmacen_ex)
		,0) preciounitario,
		ifnull(
		(SELECT DISTINCT(obtieneprecio(codProducto,dt.codNotaIngreso,2)) FROM detalleorden_compra where codproducto = dt.codProducto
    AND codAlmacen=codalmacen_ex)
		,0)as subtotal, dt.descuento1, dt.descuento2, dt.descuento3,dt.montodscto,
		 ifnull(
		(SELECT DISTINCT(obtieneprecio(codProducto,dt.codNotaIngreso,3)) FROM detalleorden_compra where codproducto = dt.codProducto
     AND codAlmacen=codalmacen_ex)
		,0)as valorventa,  ifnull(
	(SELECT DISTINCT(obtieneprecio(codProducto,dt.codNotaIngreso,4)) FROM detalleorden_compra where codproducto = dt.codProducto
   AND codAlmacen=codalmacen_ex)
	,0)as igv,  ifnull(
	(SELECT DISTINCT(obtieneprecio(codProducto,dt.codNotaIngreso,2)) FROM detalleorden_compra where codproducto = dt.codProducto
  AND codAlmacen=codalmacen_ex)
	,0)as importe,  dt.precioreal as precioreal, dt.valoreal as valoreal, dt.flete, IFNULL(dt.bonificacion,0)AS bonificacion,
  sum(dt.cantidadpendiente) as cantidadn

	FROM detallenotaingreso dt INNER JOIN notaingreso nt ON dt.codNotaIngreso=nt.codNotaIngreso
       LEFT JOIN producto p ON dt.codProducto = p.codProducto
       INNER JOIN unidadmedida u ON dt.unidadingresada = u.codUnidadMedida
       INNER JOIN detallenotaorden dc on  dc.codorden=nt.CodOrdenCompra
       INNER JOIN proveedor prov ON nt.codProveedor=prov.codProveedor
	WHERE nt.codTransaccion = 14 AND  dt.estado = 1 AND dt.codAlmacen=codalmacen_ex
       AND nt.codNotaIngreso=dc.codnota AND dc.codalmacen=codalmacen_ex AND dc.codusuario=usu AND dt.cantidadpendiente>0
GROUP BY p.codProducto
ORDER BY dt.codDetalleIngreso;
